import React from "react"
import { useSelector } from "react-redux"
import {increment,decrement,secondcounterincrement,secondcounterdecrement} from '../actions/counterActions'


export default function Counters() {
    
    const firstcounter = useSelector(state => state.firstcounterReducer )
    const secondcounter = useSelector(state => state.secondcounterReducer )
    const dispatch = useDispatch()


    return (
        
          

    <div className="App">
      <header className="App-header">
      <h1>First Counter: {firstcounter}</h1>
      <h1>Second Counter: {secondcounter}</h1>
      <button onClick={() => dispatch(increment)}>Increase First Counter</button>
      <button onClick={() => dispatch(decrement)}>Decrease First Counter</button>
      <button onClick={() => dispatch(secondcounterincrement)}>Increase Second Counter</button>
      <button onClick={() => dispatch(secondcounterdecrement)}>Decrease Second Counter</button>

